import { useState } from 'react';
import './App.css';


function App() {
  const [value,setValue]=useState("");
  const [operation, setOperation]=useState("")
  const [previousValue, setPreviousValue]=useState("")

  const handleClick=(e)=>{
    if(e.target.name=== '='){
      equal()
    }  else if(e.target.name==="Clear"){
        setValue("")

      }
      else{
        setOperation(e.target.name);
        setPreviousValue(value);
        setValue(""); 
      }

  }


  const equal=()=>{

    // setValue(eval(value).toString())
    let call;

    switch(operation){
      case "/":
    call=String(parseFloat(previousValue) / parseFloat(value));
  break;
      case "*":
     call=String(parseFloat(previousValue)*parseFloat(value));
     break;
     case "+":
     call=String(parseFloat(previousValue)+parseFloat(value));
     break; 
     case "-":
     call=String(parseFloat(previousValue)-parseFloat(value));
     break;   
     default:
    }     

    setValue(call);
    setOperation("");
    setPreviousValue("")
  }

  return (
    <div className="wrapper">

        <input className='in' type='text' value={value}/>

      <div className='bord'> 
        <button name='Clear' className='operation' onClick={handleClick}>Clear</button>
        <button name='C' className='operation'>C</button>
        <button name='%' className='operation'>%</button>
        <button name='/' className='operation' onClick={handleClick}>/</button>
        <button name='7' onClick={handleClick}>7</button>
        <button name='8'onClick={handleClick}>8</button>
        <button name='9'onClick={handleClick}>9</button>
        <button name='*' type='button' className='operation' onClick={handleClick}>*</button>
        <button name='4' onClick={handleClick}>4</button>
        <button name='5' onClick={handleClick}>5</button>
        <button name='6' onClick={handleClick}>6</button>
        <button name='-' className='operation' onClick={handleClick}>-</button>
        <button name='1' onClick={handleClick}>1</button>
        <button name='2' onClick={handleClick}>2</button>
        <button name='3'onClick={handleClick}>3</button>
        <button name='+' className='operation' onClick={handleClick}>+</button>
        <button name='0' className='zero' onClick={handleClick}>0</button>
        <button name='.' className='operation'onClick={handleClick}>.</button>
        <button name='=' className='operation' onClick={handleClick}>=</button>

        </div>
    </div>
  );
}

export default App;
